async function c(){document.getElementById("vehicle_year").value,(await(await fetch("https://vpic.nhtsa.dot.gov/api//vehicles/GetMakesForVehicleType/moto?format=json")).json()).Results.forEach(a=>{let t=document.createElement("option");t.value=a.MakeName,t.text=a.MakeName,document.getElementById("vehicle_make").appendChild(t)})}async function m(){let e=document.getElementById("vehicle_model");for(;e.firstChild;)e.removeChild(e.firstChild);let l=document.getElementById("vehicle_make").value,n=document.getElementById("vehicle_year").value;(await(await fetch(`https://vpic.nhtsa.dot.gov/api/vehicles/GetModelsForMakeYear/make/${l}/modelyear/${n}/vehicletype/moto?format=json`)).json()).Results.forEach(d=>{let o=document.createElement("option");o.value=d.Model_Name,o.text=d.Model_Name,document.getElementById("vehicle_model").appendChild(o)})}document.getElementById("vehicle_year").addEventListener("change",c);document.getElementById("vehicle_make").addEventListener("change",m);
